pub enum MoveFlag {
    Promotion {
        capture: bool,
        prom: PieceType,
    },
    Castle {
        king_side: bool,
    },
    DoublePawnPush,
    Capture {
        ep_capture: bool,
    },
    QuietMove,
}

pub enum FenBuildError {
    NotEnoughSections {
        sections: usize,
    },
    IncorrectRankAmounts {
        ranks: usize,
    },
    UnrecognizedTurn {
        turn: String,
    },
    EPSquareUnreadable {
        ep: String,
    },
    EPSquareInvalid {
        ep: String,
    },
    SquareSmallerRank {
        rank: usize,
        square: String,
    },
    SquareLargerRank {
        rank: usize,
        square: String,
    },
    UnrecognizedPiece {
        piece: char,
    },
    UnreadableMoves(num::ParseIntError),
    IllegalNumCheckingPieces {
        num: u8,
    },
    IllegalCheckState {
        piece_1: PieceType,
        piece_2: PieceType,
    },
    TooManyPawns {
        player: Player,
        num: u8,
    },
    PawnOnLastRow,
}

pub enum BoardError {
    IncorrectKingNum {
        player: Player,
        num: u8,
    },
    IncorrectKingSQ {
        player: Player,
        sq: SQ,
    },
    BadEPSquare {
        sq: SQ,
    },
}
